package com.class7;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// mustly used
		int num=10;
		while(num<=7) {
					System.out.println("HEllo");
					num++;
				}
		//rare
		int num1=10;
		do {
			System.out.println("Bye");
		num1++;
		}while (num1<=7);
		System.out.println("^^^^^^^^^^^^^");
					
				}

}
